import { MDBBtn } from "mdb-react-ui-kit";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import { getAllOrdersDetailsApi, updateStatus } from "../Services/OrderService";

function AdminOrderDetails() {
  const [processingOrders, setProcessingOrders] = useState([]);
  const [completedOrders, setCompletedOrders] = useState([]);
  const [cancelledOrders, setCancelledOrders] = useState([]);
  const [confirmedOrders, setConfirmedOrders] = useState([]);
  const [cookingOrders, setCookingOrders] = useState([]);
  const [outDeliveryOrders, setOutDeliveryOrders] = useState([]);
  const [status, setStatus] = useState("");

  const navigate = useNavigate();

  const formatdate = (d) => {
    const newdate = new Date(d);
    return newdate.toLocaleString();
  };

  const getallorders = async () => {
    try {
      const responsedata = await getAllOrdersDetailsApi();
      console.log(responsedata);
      if (responsedata.status === 200) {
        const allOrders = responsedata.data;
        const processing = allOrders.filter((order) => order.orderstatus === "PROCESSING");
        const completed = allOrders.filter((order) => order.orderstatus === "COMPLETED");
        const cancelled = allOrders.filter((order) => order.orderstatus === "CANCELLED");
        const confirmed = allOrders.filter((order) => order.orderstatus === "CONFIRMED");
        const cooking = allOrders.filter((order) => order.orderstatus === "COOKING");
        const outForDelivery = allOrders.filter((order) => order.orderstatus === "OUT_FOR_DELIVERY");

        setProcessingOrders(processing);
        setCompletedOrders(completed);
        setCancelledOrders(cancelled);
        setConfirmedOrders(confirmed);
        setCookingOrders(cooking);
        setOutDeliveryOrders(outForDelivery);
      } else {
        toast.error("Something went wrong");
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
      toast.error("Error fetching orders");
    }
  };

  const vieworder = (orderId) => {
    navigate(`/vieworderdetails/${orderId}`);
  };

  const savechanges = async (orderId) => {
    await updateStatus(orderId, status);
    getallorders();
  };

  useEffect(() => {
    getallorders();
  }, []);

  const renderOrderTable = (orders, statusOptions) => (
    <table className="table table-bordered">
      <thead>
        <tr>
          <th>Customer Id</th>
          <th>Date</th>
          <th>Address</th>
          <th>Total Quantity</th>
          <th>Total Price</th>
          <th>Payment Method</th>
          <th>Change Status</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {orders.map((order) => (
          <tr key={order.orderId} style={{ border: "1px solid #ddd" }}>
            <td>{order.userId}</td>
            <td>{formatdate(order.date)}</td>
            <td>
              {order.addressDto.addressLine}, {order.addressDto.landmark},{" "}
              {order.addressDto.city}, {order.addressDto.pincode}
            </td>
            <td>{order.totalqty}</td>
            <td>{order.totalprice}</td>
            <td>{order.paymentType}</td>
            <td>
              <select
                onChange={(e) => {
                  setStatus(e.target.value);
                }}
              >
                <option value="" disabled selected>
                  Select Status
                </option>
                {statusOptions.map((option) => (
                  <option key={option} value={option}>
                    {option}
                  </option>
                ))}
              </select>
            </td>
            <td>
              <MDBBtn size="sm" onClick={() => vieworder(order.orderId)}>
                View Order
              </MDBBtn>{" "}
              <MDBBtn size="sm" onClick={() => savechanges(order.orderId)}>
                Save Changes
              </MDBBtn>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );

  return (
    <center>
      <div className="container">
        <br />
        <h2>Order Records</h2>
        <br />
        <br />

        <div className="table-responsive">
          {[
            { orders: processingOrders, statusOptions: ["CONFIRMED", "CANCELLED"] },
            { orders: confirmedOrders, statusOptions: ["COOKING"] },
            { orders: cookingOrders, statusOptions: ["OUT_FOR_DELIVERY"] },
            { orders: outDeliveryOrders, statusOptions: ["COMPLETED", "CANCELLED"] },
            { orders: completedOrders, statusOptions: [] },
            { orders: cancelledOrders, statusOptions: [] },
          ].map(({ orders, statusOptions }, index) => (
            <div key={index}>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <h5>{statusOptions.length > 0 ? statusOptions[0] : "Other"} Orders</h5>
                  </tr>
                </thead>
                {renderOrderTable(orders, statusOptions)}
              </table>
            </div>
          ))}
        </div>
      </div>
      <ToastContainer />
    </center>
  );
}

export default AdminOrderDetails;
